perl -e 'my ($len,$total)=(0,0);my @x;while(<>){if(/>/){$Total_length++;}if(/^[\>\@]/){if($len>0){$total+=$len;push@x,$len;};$len=0;}else{s/\s//g;$len+=length($_);}}if ($len>0){$total+=$len;$mean=$total/$Total_length;push @x,$len;}@x=sort{$b<=>$a}@x; my ($count,$half)=(0,0);for (my $j=0;$j<@x;$j++){$count+=$x[$j];if(($count>=$total/2)&&($half==0)){print "Total length:$Total_length\nMean length:$mean bp\nTotal base:$total bp\nN50: $x[$j]\n";$half=$x[$j]}elsif($count>=$total*0.9){print "N90: $x[$j]\n";exit;}}' test.fasta
